    private void btnMetodoDivisionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMetodoDivisionActionPerformed
        try {
            int tamArreglo = Integer.parseInt(txtTamanioArregloDiv.getText());
            boolean esPrimo = true;
            if (tamArreglo < 0) {
                txtNumDispersion.setText("Favor escriba un número mayor que 0");
            } else if (tamArreglo % 2 == 0 && tamArreglo != 2) {
                txtNumDispersion.setText("Favor digite un número primo para el tamaño del arreglo.");
            } else {
                for (int i = 3; i * i <= tamArreglo; i += 2) {
                    if (tamArreglo % i == 0) {
                        esPrimo = false;
                    }
                }

                if (esPrimo) {
                    int clave = Integer.parseInt(txtClave.getText());
                    numDispersion = clave % tamArreglo;
                    txtNumDispersion.setText(String.valueOf(numDispersion));

                }
            }

        } catch (NumberFormatException e) {
            txtNumDispersion.setText("Favor escriba un número mayor que 0");
            System.out.println("Error: el valor del campo 'Número de dispersión' contiene caracteres no numéricos");
        }
    }//GEN-LAST:event_btnMetodoDivisionActionPerformed

